def generate_silence(start_time, end_time, bitrate, channels):
    bytes_per_sample = 2  # we always have this set to 2
    # number of bytes we need to fill for the difference between end and start times
    silence_size = int(bitrate * (end_time - start_time).total_seconds())
    frame_size = channels * silence_size
    output = []
    for i in range(0, frame_size):
        output.append(0)

    return output


def create_decoder():
    decoder = srs.OpusDecoder(
        48000,
        2,
        'C:\\Program Files\\DCS-SimpleRadio-Standalone\\opus.dll',
    )
    decoder.create()
    return decoder


def playback_audio(audio):
    playback = simpleaudio.play_buffer(
        audio_data=audio,
        num_channels=2,
        bytes_per_sample=2,
        sample_rate=48000,
    )
    playback.wait_done()


def save_audio(file_name, audio):
    if '.wav' not in file_name:
        file_name += '.wav'
    wave_file = wave.open(file_name, 'wb')
    wave_file.setnchannels(2)
    wave_file.setframerate(48000)
    wave_file.setsampwidth(2)
    wave_file.writeframes(audio)
    wave_file.close()


def decode_sample(sample):
    decoder = create_decoder()
    audio = b''
    for packet in sample:
        audio += decoder.decode(packet)
    return audio


